        var proxyData = postdata;
        if (window.device) {
          url = serviceurl;
        } else {
          url = "/websimulator/json?url=" + serviceurl;
          proxyMethod = "POST";
          proxyData = {
            method: "POST",
            body: postdata
          }
        }
        $http({
          method: proxyMethod,
          url: url,
          data: proxyData
        }).
        success(function(data, status, headers, config) {
          $.unblockUI();
          callback(data, status, headers, config);
        }).
        error(function(data, status, headers, config) {
          $.unblockUI();
          //alert("Error in service call : " + data + " Status : " + status);
          if (status == 403) { //Ticket expired.. need to relogin
            $http.post(bbauthUrl, bbauthData).success(function(data, status, headers, config) {
              $rootScope.bbticket = data.ticket;
              $rootScope.invokeBBAPI(endpoint, postdata, callback);
            }).error(function(data, status, headers, config) {
              authError(data);

            });
          }

        });

      });

    };

    /* end of BB auth function and APIs */


    // commented the actual auth URL
    if ($rootScope.demoMode) {
      var data = {
        "ticket": "b52d04c9-a0af-45e1-bdcf-fa737ba56762",
        "roles": ["STUDENT", "FACULTY", "EMPLOYEE", "STAFF"]
      }
      success(data);
    } else {
      $.blockUI({
        message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div><h3 style="color:#fff;">Authenticating</h3></div>'
      });
      $http.post(url, data).success(function(data, status, headers, config) {
        if (!data.error) {
          $rootScope.ticket = data.ticket;
          $.blockUI();
          if (defaultSystem == 'banner') {
            $rootScope.getAPI("/banneruserroles", function(roles) {
              if (roles && roles.getuserroles && roles.getuserroles[0]) {
                var uroles = [];
                $rootScope.userroles = [];
                if (roles.getuserroles[0].GOVROLE_STUDENT_IND == "Y") {
                  uroles.push("STUDENT");
                }
                if (roles.getuserroles[0].GOVROLE_EMPLOYEE_IND == "Y") {
                  uroles.push("EMPLOYEE");
                }
                if (roles.getuserroles[0].GOVROLE_FACULTY_IND == "Y") {
                  uroles.push("FACULTY");
                }
                if (roles.getuserroles[0].ALUMNI == "Y") {
                  uroles.push("ALUMNI");
                }
                if (roles.getuserroles[0].GOVROLE_FINANCE_IND == "Y") {
                  uroles.push("FINANCE");
                }
                if (roles.getuserroles[0].GOVROLE_FINAID_IND == "Y") {
                  uroles.push("FINAID");
                }
                //data.roles = uroles;
                $rootScope.bannerroles = uroles.slice();
              }
              console.log(data);
              setTimeout(function() {
                success(data);
              }, 2000);
              //success(data);
              //$.unblockUI();
              $rootScope.authBlackBoard();
            });
          } else {
            success(data);
          }


        } else {
          success(data);
          $.unblockUI();
        }
      }).error(function(data, status, headers, config) {
        error(data);
        $.unblockUI();
      });
    }
  } catch (e) {
    alert("Exception : " + e);
  }
}

