function pageprocessorEContactDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  var index = $routeParams.index
  $scope.btnlabel = "Update Contact";
  if (index == -1) {
    var priority = $rootScope.emergencycontacts.length + 1;
    $scope.emergencycontact = {
      PRIORITY: priority,
      LAST_NAME: "",
      FIRST_NAME: "",
      MIDDLE_NAME: "",
      HOUSE_NUMBER: "",
      STREET_LINE1: "",
      STREET_LINE2: "",
      STREET_LINE3: "",
      STREET_LINE4: "",
      CITY: "",
      STATE_CODE: "",
      COUNTRY_CODE: "",
      ZIP: "",
      PHONE_AREA: "",
      PHONE_NUMBER: "",
      PHONE_EXTN: "",
      PHONE_COUNTRY_CODE: "",
      RELATIONSHIP_CODE: ""
    };
    $scope.btnlabel = "Add Contact";
  } else {
    var selectedContact = $rootScope.emergencycontacts[index];
    $scope.emergencycontact = selectedContact;
  }

  $scope.showalert = function(message, title) {
    if (window.device) {
      navigator.notification.alert(message, null, title, null);
    } else {
      apprise(message, {
        'verify': false,
        'textYes': "Ok"
      }, function(r) {});
    }
  }
  $scope.validateRequired = function() {
    if ($scope.emergencycontact.RELATIONSHIP_CODE == "" || $scope.emergencycontact.RELATIONSHIP_CODE == null) {
      $scope.showalert("Please select Relationship", "Emergency Contact");
      return false;
    }
    if (!$scope.emergencycontact.FIRST_NAME || $scope.emergencycontact.FIRST_NAME.trim() == "") {
      $scope.showalert("Please enter First name", "Emergency Contact");
      return false;
    } else {
      if (/^[a-zA-Z0-9- ]*$/.test($scope.emergencycontact.FIRST_NAME.trim()) == false) {
        $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in name.');
        return false;
      }
    }

    if (($scope.emergencycontact.MIDDLE_NAME != "" && $scope.emergencycontact.MIDDLE_NAME != null) &&
      /^[a-zA-Z0-9- ]*$/.test($scope.emergencycontact.MIDDLE_NAME.trim()) == false) {
      $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in name.');
      return false;
    }

    if (!$scope.emergencycontact.LAST_NAME || $scope.emergencycontact.LAST_NAME.trim() == "") {
      $scope.showalert("Please enter Last name", "Emergency Contact");
      return false;
    } else {
      if (/^[a-zA-Z0-9- ]*$/.test($scope.emergencycontact.LAST_NAME.trim()) == false) {
        $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in name.', "Emergency Contact");
        return false;
      }
    }

    if (($scope.emergencycontact.STREET_LINE1 != "" && $scope.emergencycontact.STREET_LINE1 != null) && /^[a-zA-Z0-9- #]*$/.test($scope.emergencycontact.STREET_LINE1.trim()) == false) {
      $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in address.', "Emergency Contact");
      return false;
    }
    if (($scope.emergencycontact.STREET_LINE2 != "" && $scope.emergencycontact.STREET_LINE2 != null) && /^[a-zA-Z0-9- #]*$/.test($scope.emergencycontact.STREET_LINE2.trim()) == false) {
      $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in address.', "Emergency Contact");
      return false;
    }
    if (($scope.emergencycontact.STREET_LINE3 != "" && $scope.emergencycontact.STREET_LINE3 != null) && /^[a-zA-Z0-9- #]*$/.test($scope.emergencycontact.STREET_LINE3.trim()) == false) {
      $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in address.', "Emergency Contact");
      return false;
    }
    if (($scope.emergencycontact.STREET_LINE4 != "" && $scope.emergencycontact.STREET_LINE4 != null) && /^[a-zA-Z0-9- #]*$/.test($scope.emergencycontact.STREET_LINE4.trim()) == false) {
      $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in address.', "Emergency Contact");
      return false;
    }
    if (($scope.emergencycontact.ZIP != "" && $scope.emergencycontact.ZIP != null) && /^[a-zA-Z0-9- ]*$/.test($scope.emergencycontact.ZIP.trim()) == false) {
      $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in zip code.', "Emergency Contact");
      return false;
    }
    if ($scope.emergencycontact.STREET_LINE1 && !$scope.emergencycontact.CITY) {
      $scope.showalert("City is required if Street Line1 is specified.", "Emergency Contact");
      return false;
    }
    if ($scope.emergencycontact.COUNTRY_CODE != "" && $scope.emergencycontact.COUNTRY_CODE != null) {
      var city = $scope.emergencycontact.CITY == null ? "" : $scope.emergencycontact.CITY;
      if (city == "" || city == null || city.trim() == "") {
        $scope.showalert("Please enter City", "Emergency Contact");
        return false;
      }
      if ($scope.emergencycontact.STREET_LINE1 == "" || $scope.emergencycontact.STREET_LINE1 == null) {
        $scope.showalert("Please enter Address line 1", "Emergency Contact");
        return false;
      }
      if ($scope.emergencycontact.PHONE_NUMBER == "" || $scope.emergencycontact.PHONE_NUMBER == null) {
        $scope.showalert("Please enter Phone number", "Emergency Contact");
        return false;
      }
    }
    if (($scope.emergencycontact.STREET_LINE1 || $scope.emergencycontact.STREET_LINE2 ||
        $scope.emergencycontact.STREET_LINE3 || $scope.emergencycontact.STREET_LINE4) && $scope.emergencycontact.CITY) {
      if (!$scope.emergencycontact.STATE_CODE && !$scope.emergencycontact.COUNTRY_CODE) {
        $scope.showalert("If Address and City are provided, then State or Nation is required.", "Emergency Contact");
        return false;
      }
    }
    if ($scope.emergencycontact.STATE_CODE && ($scope.emergencycontact.ZIP == "" || $scope.emergencycontact.ZIP == null)) {
      $scope.showalert("Zip code is required when State Code is provided. ", "Emergency Contact");
      return false;
    }
    return true
  }
  $scope.updateContact = function() {
    if ($scope.validateRequired() == false) {
      return;
    }
    var postdata = "priority=" + $scope.emergencycontact.PRIORITY + "&";
    postdata += "oldPriority=" + $scope.emergencycontact.PRIORITY + "&";
    postdata += "lastName=" + ($scope.emergencycontact.LAST_NAME == null ? "" : $scope.emergencycontact.LAST_NAME) + "&";
    postdata += "firstName=" + ($scope.emergencycontact.FIRST_NAME == null ? "" : $scope.emergencycontact.FIRST_NAME) + "&";
    postdata += "middleName=" + ($scope.emergencycontact.MIDDLE_NAME == null ? "" : $scope.emergencycontact.MIDDLE_NAME) + "&";
    postdata += "surnamePrefix=" + ($scope.emergencycontact.SURNAME_PREFIX == null ? "" : $scope.emergencycontact.SURNAME_PREFIX) + "&";
    postdata += "houseNumber=" + ($scope.emergencycontact.HOUSE_NUMBER == null ? "" : $scope.emergencycontact.HOUSE_NUMBER) + "&";
