      if (window.device) {
        navigator.notification.alert("Please select term code.", null, "Look Up Classes", null);
      } else {
        apprise("Please select term code", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {

        });
      }
      return;
    }
    if ($scope.selectedSubject == "" && $scope.selectedInstructor == "") {
      if (window.device) {
        navigator.notification.alert("Please select Subject or Instructor.", null, "Look Up Classes", null);
      } else {
        apprise("Please select Subject or Instructor.", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {

        });
      }
      return;
    }
    var searchURL = "/bannerclasslookup/termCode=" + $scope.selectedvalue +
      "&subjectCode=" + $scope.selectedSubject +
      "&courseNumber=" + $scope.courseNumber +
      "&partTermCode=" + $scope.partTermCode +
      "&facultyPidm=" + $scope.selectedInstructor +
      "&courseTitle=" + $scope.courseTitle +
      "&creditFrom=" + $scope.creditFrom +
      "&creditTo=" + $scope.creditTo;

    $rootScope.getAPI( searchURL, function(data) {
      if (data.CLASSLOOKUP.length == 0) {
        if (window.device) {
          navigator.notification.alert("No record found.", null, "Look Up Classes", null);
        } else {
          apprise("No record found.", {
            'verify': false,
            'textYes': "Ok"
          }, function(r) {

          });
        }
      } else {
        $rootScope.classlookup = data.CLASSLOOKUP;
        $location.path("/app/LookupClasses/LookupClassesDetails");
      }
    });
  }
}

